nasal.load:
  print("loading doors");
   if (!contains(globals, "scenery")) globals["scenery"] = {};
   if (!contains(scenery, "LOWI")) scenery["LOWI"] = {};
   if (!contains(scenery.LOWI, "LOJO_oamtc_helicopter_base")) scenery.LOWI["LOJO_oamtc_helicopter_base"] = [nil];
   scenery.LOWI.LOJO_oamtc_helicopter_base[0] =
	 { door :
	   [aircraft.door.new("/scenery/LOWI/LOJO_oamtc_helicopter_base[0]/door[0]", 3),
		aircraft.door.new("/scenery/LOWI/LOJO_oamtc_helicopter_base[0]/door[1]", 3),
		aircraft.door.new("/scenery/LOWI/LOJO_oamtc_helicopter_base[0]/door[2]", 3),
		aircraft.door.new("/scenery/LOWI/LOJO_oamtc_helicopter_base[0]/door[3]", 3),
		aircraft.door.new("/scenery/LOWI/LOJO_oamtc_helicopter_base[0]/door[4]", 3)

	   ]
	 };
	print("setting lights");
	var omatcpadli = 121.875;
	var oamtclon = 1.48;

	print("setting lights finished - use 121.875 Mhz on COM2 for LOJO - OAMTC main helipad lights");
	var loop_id = 0;


	var loop = func(id) {
			id == loop_id or return;
			var padradi = getprop("/instrumentation/comm[1]/frequencies/selected-mhz");
			var oamtcsun = getprop("/sim/time/sun-angle-rad");
				#print ("padradi  ", padradi);
				#print ("oamtcsun  ", oamtcsun);

			if ((padradi == omatcpadli) and (oamtcsun > oamtclon)){		
				setprop("/scenery/LOWI/LOJO_oamtc_helicopter_base[0]/light[0]", 1);
			}else {
				setprop("/scenery/LOWI/LOJO_oamtc_helicopter_base[0]/light[0]", 0);
			};

			if (oamtcsun > oamtclon){		
				setprop("/scenery/LOWI/LOJO_oamtc_helicopter_base[0]/light[1]", 1);
			}else {
				setprop("/scenery/LOWI/LOJO_oamtc_helicopter_base[0]/light[1]", 0);
			};

	settimer(func { loop(id) }, 2);
		}
		loop(loop_id += 1);
